		/**
		 * Jitsi_Meet_WP_Admin constructor.
		 */
		public function __construct() {
			add_action( 'admin_menu', [ $this, 'jitsi_meet_wp_admin_menu_page' ] );
			add_action( 'admin_init', [ $this, 'display_notices' ] );
			add_action( 'wp_ajax_jitsi_meet_wp_review_notice', [ $this, 'handle_review_notice' ] );
			add_action( 'wp_ajax_jitsi_meet_wp_affiliate_notice', [ $this, 'handle_affiliate_notice' ] );
			$this->jitsi_meet_wp_settings_files();
		}

		/**
		 * Display notices
		 *
		 * @return null
		 */
		public function display_notices() {
			if ( ! get_option( 'webinar-and-video-conference-with-jitsi-meet_allow_tracking' ) ) {
				return;
			}

			// Review notice.
			if ( 'off' !== get_option( 'jitsi_meet_wp_review_notice_interval', 'on' ) && 'off' !== get_transient( 'jitsi_meet_wp_review_notice_interval' ) ) {

				ob_start();
				jitsi_meet_wp()->get_template( 'admin/review-notice' );
				$notice_html = ob_get_clean();

				jitsi_meet_wp()->add_notice( 'info jitsi-meet-wp-review-notice', $notice_html );
			} else {
				// Affiliate notice.
				if ( 'off' !== get_option( 'jitsi_meet_wp_affiliate_notice_interval', 'on' )
					&& 'off' !== get_transient( 'jitsi_meet_wp_affiliate_notice_interval' ) ) {

					ob_start();
					jitsi_meet_wp()->get_template( 'admin/affiliate-notice' );
					$notice_html = ob_get_clean();

					jitsi_meet_wp()->add_notice( 'info jitsi-meet-wp-affiliate-notice', $notice_html );
				}
			}
		}

		/**
		 * Handle review notice
		 *
		 * @return  void
		 */
		public function handle_review_notice() {

			// Checks WordPress Nonce.
			if ( ! isset( $_REQUEST['nonce'] ) || ! wp_verify_nonce( sanitize_text_field( wp_unslash( $_REQUEST['nonce'] ) ), 'jitsi_meet_admin_nonce' ) ) {
				wp_send_json_error( __( 'Invalid request', 'jitsi-meet-wp' ) );
				wp_die();
			}

			// Checks if the user has permission to do this action.
			if ( ! current_user_can( 'manage_options' ) ) {
				wp_send_json_error( __( 'You do not have permission to do this action', 'jitsi-meet-wp' ) );
			}

			$value = ! empty( $_REQUEST['data'] ) ? sanitize_text_field( wp_unslash( $_REQUEST['data'] ) ) : 7;

			if ( 'hide_notice' === $value ) {
				update_option( 'jitsi_meet_wp_review_notice_interval', 'off' );
			} elseif ( is_numeric( $value ) ) {
				$days = $value * DAY_IN_SECONDS;
				set_transient( 'jitsi_meet_wp_review_notice_interval', 'off', $days );
			}

			update_option( sanitize_key( 'jitsi_meet_wp_notices' ), [] );
		}

		/**
		 * Handle affiliate notice
		 *
		 * @return  void
		 */
		public function handle_affiliate_notice() {

			// Checks WordPress Nonce.
			if ( ! isset( $_REQUEST['nonce'] ) || ! wp_verify_nonce( sanitize_text_field( wp_unslash( $_REQUEST['nonce'] ) ), 'jitsi_meet_admin_nonce' ) ) {
				wp_send_json_error( __( 'Invalid request', 'jitsi-meet-wp' ) );
				wp_die();
			}

			// Checks if the user has permission to do this action.
			if ( ! current_user_can( 'manage_options' ) ) {
				wp_send_json_error( __( 'You do not have permission to do this action', 'jitsi-meet-wp' ) );
			}

			$value = ! empty( $_REQUEST['data'] ) ? sanitize_text_field( wp_unslash( $_REQUEST['data'] ) ) : 7;

			if ( 'hide_notice' === $value ) {
				update_option( 'jitsi_meet_wp_affiliate_notice_interval', 'off' );
			} elseif ( is_numeric( $value ) ) {
				$days = $value * DAY_IN_SECONDS;
				set_transient( 'jitsi_meet_wp_affiliate_notice_interval', 'off', $days );
			}

			update_option( sanitize_key( 'jitsi_meet_wp_notices' ), [] );
		}

		/**
		 * Jitsi meet settings files
		 *
		 * @return  void
		 */
		public function jitsi_meet_wp_settings_files() {
			require_once JITSI_MEET_WP_FILE_PATH . 'inc/admin/admin-settings.php';
			if ( get_option( 'jitsi_meet_welcome_redirect', false ) && get_option( 'jitsi_meet_welcome_redirect', false ) !== 'occured' ) {
				// Checks if the user has permission to do this action.
				if ( ! current_user_can( 'manage_options' ) ) {
					wp_send_json_error( __( 'You do not have permission to do this action', 'jitsi-meet-wp' ) );
				}
				//phpcs:ignore
				$query_string = $_SERVER['QUERY_STRING'];
				$current_page = trim( str_replace( 'page=', '', $query_string ) );

				if ( ! isset( $current_page ) || ! ( 'jitsi-meet-welcome' === $current_page ) ) {

					wp_safe_redirect( admin_url( 'admin.php?page=jitsi-meet-welcome' ) );
					die();
				}
				update_option( 'jitsi_meet_welcome_redirect', 'occured' );
			}
		}

